var $lockStatus=$("#lockStatus");
var $securityToken=$("#SecurityToken");
var $process=$("#process");
var checkLock=function(){try{var e=new ActiveXObject("NLLIB.clsNovinLock");
var f=e.GetSerial();
if(e.ErrNo==0){$lockStatus.attr("class","btn btn-info").html("شناسه متصل می باشد!");
$securityToken.val(f);
$process.removeAttr("disabled")
}else{$lockStatus.attr("class","btn btn-warning").html("شناسه متصل نمی باشد.");
$securityToken.val("");
$process.attr("disabled","disabled")
}}catch(d){$lockStatus.attr("class","btn btn-danger").html("درایور شناسه نصب نمی باشد.");
$securityToken.val("");
$process.attr("disabled","disabled")
}setTimeout("checkLock()",1000)
};